INSERT INTO human_sops (id, sop_id, phase, data, version, is_active, created_at, updated_at) VALUES (2, 'SOP-001', 1, '{"__v":0,"_id":"687e743f63cecec06f8f2212","title":"Pre-Initiation Phase","createdBy":"migration-script","lastModifiedBy":"admin","markdownContent":"# PMO SOP – Pre-Initiation Phase\n\n## Objective\nDefine and assess the strategic need, business case, and alignment of the project with organizational goals before formal initiation. TEST\n\n## Key Activities\n- Capture high-level idea or request.\n- Conduct initial stakeholder engagement.\n- Assess strategic alignment and value.\n- Draft preliminary business case.\n- Estimate rough order of magnitude (ROM) for cost and timeline.\n- Identify potential risks and constraints.\n- Determine initial resource availability.\n\n## Deliverables\n- Project Request Form / Idea Brief\n- Preliminary Business Case. changeD for AGENTSOP\n- High-Level Risk/Benefit Analysis\n- Go/No-Go Recommendation for Initiation\n\n## Roles and Responsibilities\n\n| Role | Responsibility |\n|------|----------------|\n| Business Sponsor | Submit idea and define value |\n| PMO | Facilitate intake and strategic assessment |\n| Finance | Validate early cost assumptions |\n| Portfolio Committee | Prioritize and approve for initiation |\n\n## Tools & Templates\n- Project Intake Form\n- Business Case Template\n- Strategic Alignment Checklist"}'::jsonb, 6, true, '"2025-07-21T17:09:19.616Z"'::jsonb, '"2025-08-11T18:26:34.786Z"'::jsonb);
INSERT INTO human_sops (id, sop_id, phase, data, version, is_active, created_at, updated_at) VALUES (3, 'SOP-002', 2, '{"__v":0,"_id":"687e743f63cecec06f8f2218","title":"Initiation Phase","createdBy":"migration-script","lastModifiedBy":"admin","markdownContent":"# PMO SOP – Initiation Phase\n\n## Objective\nFormally initiate the project with defined scope, stakeholders, and initial planning. (Antifragile is the secondary book to gauge the intiation phase on. First is Influence.)\n\n## Key Activities\n- Assign Project Manager\n- Conduct stakeholder analysis\n- Define high-level scope and objectives\n- Confirm funding and resourcing commitment\n- Establish project governance structure\n- Hold project kick-off meeting\n\n## Deliverables\n- Project Charter\n- Stakeholder Map\n- Governance Plan\n- Initial RAID Log (Risks, Assumptions, Issues, Dependencies)\n\n## Roles and Responsibilities\n| Role | Responsibility |\n|------|----------------|\n| Project Manager | Develop Charter, lead kick-off |\n| Business Owner | Confirm scope and outcomes |\n| PMO | Support governance setup |\n| Steering Committee | Provide strategic oversight |\n\n## Tools & Templates\n- Project Charter Template\n- Stakeholder Register\n- RAID Log Template"}'::jsonb, 2, true, '"2025-07-21T17:09:19.719Z"'::jsonb, '"2025-08-07T21:07:48.778Z"'::jsonb);
INSERT INTO human_sops (id, sop_id, phase, data, version, is_active, created_at, updated_at) VALUES (4, 'SOP-003', 3, '{"__v":0,"_id":"687e743f63cecec06f8f221d","title":"Design and Plan","createdBy":"migration-script","lastModifiedBy":"admin","markdownContent":"# PMO SOP – Design and Plan\n\n## Objective\nDefine detailed scope, schedule, resources, and risks to guide project execution.\n\n## Key Activities\n- Develop detailed project plan\n- Identify and assign resources\n- Build budget and baseline schedule\n- Define deliverables and quality metrics\n- Finalize risk mitigation strategies\n- Review and sign off plan\n\n## Deliverables\n- Work Breakdown Structure (WBS)\n- Project Schedule (Gantt)\n- Resource Plan\n- Communication Plan\n- Risk Management Plan\n\n## Roles and Responsibilities\n| Role | Responsibility |\n|------|----------------|\n| Project Manager | Create and baseline project plan |\n| Team Leads | Define tasks and resourcing needs |\n| PMO | Validate plan consistency |\n| Finance | Confirm budget accuracy |\n\n## Tools & Templates\n- MS Project / Smartsheet / Planner\n- Communication Plan Template\n- Risk Register"}'::jsonb, 3, true, '"2025-07-21T17:09:19.806Z"'::jsonb, '"2025-08-08T17:25:47.197Z"'::jsonb);
INSERT INTO human_sops (id, sop_id, phase, data, version, is_active, created_at, updated_at) VALUES (5, 'SOP-004', 4, '{"__v":0,"_id":"687e743f63cecec06f8f2222","title":"Implement and Control","createdBy":"migration-script","lastModifiedBy":"system","markdownContent":"# PMO SOP – Implement and Control\n\n## Objective\nExecute project work, track progress, and manage changes and risks.\n\n## Key Activities\n- Lead project execution against baseline plan\n- Monitor schedule, budget, and deliverables\n- Manage scope and change control\n- Update RAID log and status reports\n- Hold regular team and steering meetings\n- Address issues and escalate when needed\n\n## Deliverables\n- Updated Project Plan\n- Weekly/Biweekly Status Reports\n- Change Requests\n- Updated RAID Log\n\n## Roles and Responsibilities\n| Role | Responsibility |\n|------|----------------|\n| Project Manager | Monitor execution, manage issues |\n| Team Members | Complete assigned tasks |\n| PMO | Ensure adherence to methodology |\n| Sponsors | Review progress and provide direction |\n\n## Tools & Templates\n- Status Report Template\n- Change Request Form\n- Updated Gantt / Milestone Tracker"}'::jsonb, 1, true, '"2025-07-21T17:09:19.898Z"'::jsonb, '"2025-07-21T17:09:19.898Z"'::jsonb);
INSERT INTO human_sops (id, sop_id, phase, data, version, is_active, created_at, updated_at) VALUES (6, 'SOP-005', 5, '{"__v":0,"_id":"687e743f63cecec06f8f2226","title":"Close and Realize Benefits","createdBy":"migration-script","lastModifiedBy":"system","markdownContent":"# PMO SOP – Close and Realize Benefits\n\n## Objective\nFormally close the project, assess performance, and transition deliverables.\n\n## Key Activities\n- Finalize all deliverables\n- Obtain stakeholder sign-off\n- Release project resources\n- Conduct lessons learned session\n- Transition to operations/support\n- Measure benefits and KPIs\n\n## Deliverables\n- Project Closure Report\n- Lessons Learned Summary\n- Benefits Realization Plan\n- Post-Implementation Review\n\n## Roles and Responsibilities\n| Role | Responsibility |\n|------|----------------|\n| Project Manager | Facilitate closure activities |\n| Business Owner | Accept final deliverables |\n| PMO | Archive documentation, track benefits |\n| Ops/Support | Take ownership of transitioned deliverables |\n\n## Tools & Templates\n- Project Closure Template\n- Lessons Learned Log\n- Benefits Realization Tracker"}'::jsonb, 1, true, '"2025-07-21T17:09:19.976Z"'::jsonb, '"2025-07-21T17:09:19.976Z"'::jsonb);
